import {Calendar,dateFnsLocalizer} from "react-big-calendar";
import format from "date-fns/format"
import parse from "date-fns/parse"
import startOfWeek  from "date-fns/startOfWeek";
import getDay from "date-fns/getDay"
import "react-big-calendar/lib/css/react-big-calendar.css"
import React, {useState} from "react";
import DatePicker from "react-datepicker";

class CalendarEvent {
    title: string;
    allDay: boolean;
    start: Date;
    endDate: Date;
    desc: string;
    resourceId?: string | undefined;
    tooltip?: string | undefined;

    constructor(_title: string, _start: Date, _endDate: Date, _allDay?: boolean, _desc?: string, _resourceId?: string) {
        this.title = _title;
        this.allDay = _allDay || false;
        this.start = _start;
        this.endDate = _endDate;
        this.desc = _desc || '';
        this.resourceId = _resourceId;
    }
}

 const events = [
    {
        title: "Big Meeting",
        allDay: true,
        start: new Date(2022, 2, 1),
        end: new Date(2022, 2, 2),
    },
    {
        title: "Vacation",
        start: new Date(2022, 2, 2),
        end: new Date(2022, 2, 3),
    },
    {
        title: "Conference",
        start: new Date(2022, 2, 20),
        end: new Date(2022, 2, 20),
    },
];

  const locales = {
    "en-US": require("date-fns/locale/en-US")
  }

  const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
  })


const CalenderPage = () => {
  const [newEvent,setNewEvent] = useState({title:"",start:"",end:""})
  const [allEvents,setAllEvents] = useState(events)

