    setAllEvents([...allEvents,setNewEvent])
  }

  return (
    <div>
      <h1>Calendar</h1>
      <h2>Add New Event</h2>
    <Calendar 
    localizer={localizer}
    events={allEvents}
    startAccessor="start"
    endAccessor="end" 
    style = {{height:500,margin:"50px"}}
    />
   </div>
  )
}

export default CalenderPage
