import {Calendar,dateFnsLocalizer} from "react-big-calendar";
import format from "date-fns/format"
import parse from "date-fns/parse"
import startOfWeek  from "date-fns/startOfWeek";
import getDay from "date-fns/getDay"
import "react-big-calendar/lib/css/react-big-calendar.css"
import {useState} from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

//Dummy data
 const events = [
    {
        title: "Big Meeting",
        allDay: true,
        start: new Date(2022, 2, 1),
        end: new Date(2022, 2, 2),
    },
    {
        title: "Vacation",
        start: new Date(2022, 2, 2),
        end: new Date(2022, 2, 3),
    },
    {
        title: "Conference",
        start: new Date(2022, 2, 20),
        end: new Date(2022, 2, 20),
    },
];

  const locales = {
    "en-US": require("date-fns/locale/en-US")
  }

  const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
  })


const CalenderPage = () => {


  const [newEvent,setNewEvent] = useState({title:"",start:"",end:""})
  const [allEvents,setAllEvents] = useState(events)

  function handleAddEvent(){
    setAllEvents([...allEvents,setNewEvent])
  }

  return (
    <div >
      <h1>Calendar</h1>
      <h2>Add New Event</h2>
      <div style={{display: 'flex',alignItems:center, lineHeight: '40px'}}>
          <input type="text" placeholder="Add Title" style={{ width: "20%", marginRight: "10px" }} value={newEvent.title} onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })} />
                <DatePicker 
                placeholderText="Start Date" 
                style={{ marginRight: "10px" }} 
                selected={newEvent.start} 
                onChange={(start) => setNewEvent({ ...newEvent, start })} 
                />
                <DatePicker 
                placeholderText="End Date" 
                selected={newEvent.end} 
                onChange={(end) => setNewEvent({ ...newEvent, end })} 
                />
                <button style={{ marginTop: "10px" }} onClick={handleAddEvent}>
                    Add Event
                </button>
      </div>
    <Calendar 
    localizer={localizer}
    events={allEvents}
    startAccessor="start"
    endAccessor="end" 
    style = {{height:500,margin:"50px"}}
    />
   </div>
  )
}

export default CalenderPage
